    sed "s~\${ACTUALBUDGET_PROXY_PROMETHEUS_PASSWORD}~$ACTUALBUDGET_PROXY_PROMETHEUS_PASSWORD~g" |
    sed "s~\${CERTBOT_PROXY_PROMETHEUS_PASSWORD}~$CERTBOT_PROXY_PROMETHEUS_PASSWORD~g" |
